/**
 ** a module to fitting section on window
 **
 */

// Config
var config = {
	element : '.window__fit',
	container : ''
}

 var element = document.querySelectorAll(config.element)
 
 var fitwindow = {
 	getHeight : function() {
 		var height;
 		vh = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;

 		_.each(element ,function(element) {
	 		element.style.height = vh + 'px';

 		})

 	},

 	resize: function() {
 		window.addEventListener('resize', function() {
 			fitwindow.getHeight();
 		})
 	},
 
 	init: function() {
 		if(typeof element == undefined || element.length <= 0) {
 			console.log('return')
 			return;
 		}
 		this.getHeight();
 		this.resize();
 	}
 }
 window.onload = fitwindow.init();

(function( global ) {

	 var Animated = function (selector) {

	 	this.selector =  document.querySelectorAll(selector);
	 	return this;
	 };

	Animated.prototype.bounce = function(coorX, coorY) {
		var tween =TweenMax.from(this.selector, 1, {
			x: coorX,
			y: coorY,
			ease: Bounce.easeOut
		})
		return tween;
	};

	Animated.prototype.fading = function(time) {
		console.log(this.selector)
		var tween = TweenMax.fromTo(this.selector, time, {
			opacity: 0
		},
		{
			opacity: 1
		});
		return tween;

	};


	var animated = function(selector, context) {
		return new Animated(selector, context);

	}



	global.animated = animated;


})( this );




