(function( global ) {

	 var Animated = function (selector) {

	 	this.selector =  document.querySelectorAll(selector);
	 	return this;
	 };

	Animated.prototype.bounce = function(coorX, coorY) {
		var tween =TweenMax.from(this.selector, 1.5, {
			x: coorX,
			y: coorY,
			ease: Bounce.easeOut
		})
		return tween;
	};

	Animated.prototype.fading = function(time) {
		var tween = TweenMax.fromTo(this.selector, time, {
			opacity: 0
		},
		{
			opacity: 1
		});
		return tween;

	};



	var animated = function(selector, context) {
		return new Animated(selector, context);

	}



	global.animated = animated;


})( this );




(function() {

	// Constructor for modal

	var container, dialog;
	var modal_toggle  = document.querySelectorAll('.toggle--modal');
	var close_toggle   = document.querySelectorAll('.toggle--close');
	var appear     	     = new TimelineMax();
	var disapear 	     = new TimelineMax();
	var hash = window.location.hash;


	function modalShow (target)
	{
		 dialog = target.children[0];
		 close =  dialog.lastElementChild;
		target.classList.add('active');
		 var container_appear  = TweenMax.fromTo(target, 0.3, {
								 	display: 'none',
								 	scale: 0
								 },
								 {
								 	display: 'block',
								 	scale: 1
								 }
								 );

		 var dialog_appear		= 	TweenMax.fromTo(dialog, 0.5, {
									 	y: -300,
									 	opacity: 0
									 },
									 {
									 	y: 0,
									 	opacity: 1

									 })
		 appear.add(container_appear)
		 		.add(dialog_appear)		
	}	

			// triggering with hash
			if(hash.length > 0)
			{
				 container = document.querySelector(hash);


				modalShow(container);	
			}	

	_.each(modal_toggle, function (modal) {

		modal.addEventListener('click', function (e) {
			// e.preventDefault();

			var target = modal.getAttribute('data-target');
				 container = document.getElementById(target);
			modalShow(container);

		});

	})


	// Close Modal

	_.each(close_toggle, function (close) {
		close.addEventListener('click', function(e) {
			// e.preventDefault();
			
			dialog = this.parentElement
			container 	   = this.parentElement.parentElement

			 var container_disapear  = TweenMax.fromTo(container, 0.3, {
									 	display: 'block',
									 	scale: 1
									 },
									 {
									 	display: 'none',
									 	scale: 0
									 }
									 );

			 var dialog_disapear		= 	TweenMax.fromTo(dialog, 0.5, {
										 	y: 0,
										 	opacity: 1

										 },
										 {
										 	y: -300,
										 	opacity: 0

										 })


			 disapear.add(dialog_disapear)
			 		  .add(container_disapear)

		})
	});




}())

/**
 ** a module to fitting section on window
 **
 */

// Config
var config = {
	element : '.window__fit',
	container : ''
}

 var element = document.querySelectorAll(config.element)
 
 var fitwindow = {
 	getHeight : function() {
 		var height;
 		vh = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;

 		_.each(element ,function(element) {
	 		element.style.height = vh + 'px';

 		})

 	},

 	resize: function() {
 		window.addEventListener('resize', function() {
 			fitwindow.getHeight();
 		})
 	},
 
 	init: function() {
 		if(typeof element == undefined || element.length <= 0) {
 			console.log('return')
 			return;
 		}
 		this.getHeight();
 		this.resize();
 	}
 }
 window.onload = fitwindow.init();

